// 3. Helper function used by script.js
function getQuestionsForModule(moduleKey, difficulty, count) {
  const module = mathModules[moduleKey];
  if (!module || !module[difficulty]) {
    return [];
  }
  const pool = module[difficulty];
  // Shuffle and pick up to `count` questions
  const shuffled = [...pool].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, count).map(q => ({
    question: q.question,
    options: q.options,
    correct: q.correctAnswer + 1  // Convert 0-based → 1-based for compatibility with script.js
  }));
}

